import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useQuery } from 'react-query';
import { toast } from 'react-hot-toast';
import { 
  Clock, 
  CheckCircle, 
  XCircle, 
  Download, 
  BarChart3,
  FileText,
  Code,
  Zap
} from 'lucide-react';
import { analysisAPI } from '../services/api';

const ProgressTracker = () => {
  const { taskId } = useParams();
  const navigate = useNavigate();
  const [pollingInterval, setPollingInterval] = useState(2000);

  const { data: status, error, isLoading } = useQuery(
    ['taskStatus', taskId],
    () => analysisAPI.getTaskStatus(taskId),
    {
      refetchInterval: pollingInterval,
      refetchIntervalInBackground: true,
      retry: false,
    }
  );

  useEffect(() => {
    if (status?.status === 'completed') {
      setPollingInterval(false);
      toast.success('Analysis completed successfully!');
      navigate(`/results/${taskId}`);
    } else if (status?.status === 'failed') {
      setPollingInterval(false);
      toast.error('Analysis failed. Please try again.');
    }
  }, [status?.status, taskId, navigate]);

  if (isLoading) {
    return (
      <div className="card max-w-2xl mx-auto">
        <div className="text-center py-8">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading task status...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="card max-w-2xl mx-auto">
        <div className="text-center py-8">
          <XCircle className="h-12 w-12 text-error-500 mx-auto mb-4" />
          <h3 className="text-lg font-semibold text-gray-900 mb-2">Error Loading Task</h3>
          <p className="text-gray-600 mb-4">{error.message}</p>
          <button
            onClick={() => navigate('/')}
            className="btn-primary"
          >
            Back to Home
          </button>
        </div>
      </div>
    );
  }

  const getStatusIcon = () => {
    switch (status?.status) {
      case 'pending':
        return <Clock className="h-8 w-8 text-warning-500" />;
      case 'running':
        return <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>;
      case 'completed':
        return <CheckCircle className="h-8 w-8 text-success-500" />;
      case 'failed':
        return <XCircle className="h-8 w-8 text-error-500" />;
      default:
        return <Clock className="h-8 w-8 text-gray-500" />;
    }
  };

  const getStatusColor = () => {
    switch (status?.status) {
      case 'pending':
        return 'text-warning-600 bg-warning-50';
      case 'running':
        return 'text-primary-600 bg-primary-50';
      case 'completed':
        return 'text-success-600 bg-success-50';
      case 'failed':
        return 'text-error-600 bg-error-50';
      default:
        return 'text-gray-600 bg-gray-50';
    }
  };

  const getStepIcon = (step) => {
    const stepIcons = {
      'initializing': <Zap className="h-5 w-5" />,
      'cloning repository': <Download className="h-5 w-5" />,
      'analyzing repository structure': <FileText className="h-5 w-5" />,
      'generating unit tests': <Code className="h-5 w-5" />,
      'running tests and generating coverage': <BarChart3 className="h-5 w-5" />,
      'preparing final results': <CheckCircle className="h-5 w-5" />,
      'analysis completed': <CheckCircle className="h-5 w-5" />,
    };
    return stepIcons[step?.toLowerCase()] || <Clock className="h-5 w-5" />;
  };

  const steps = [
    { name: 'Initializing', key: 'initializing' },
    { name: 'Cloning Repository', key: 'cloning repository' },
    { name: 'Analyzing Structure', key: 'analyzing repository structure' },
    { name: 'Generating Tests', key: 'generating unit tests' },
    { name: 'Running Tests', key: 'running tests and generating coverage' },
    { name: 'Preparing Results', key: 'preparing final results' },
    { name: 'Completed', key: 'analysis completed' },
  ];

  const currentStepIndex = steps.findIndex(step => 
    status?.current_step?.toLowerCase().includes(step.key)
  );

  return (
    <div className="card max-w-4xl mx-auto">
      {/* Header */}
      <div className="text-center mb-8">
        <div className="flex justify-center mb-4">
          {getStatusIcon()}
        </div>
        <h2 className="text-2xl font-bold text-gray-900 mb-2">
          Analysis in Progress
        </h2>
        <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${getStatusColor()}`}>
          {status?.status?.charAt(0).toUpperCase() + status?.status?.slice(1)}
        </div>
      </div>

      {/* Progress Bar */}
      <div className="mb-8">
        <div className="flex justify-between text-sm text-gray-600 mb-2">
          <span>Progress</span>
          <span>{status?.progress_percentage || 0}%</span>
        </div>
        <div className="progress-bar">
          <div 
            className="progress-fill"
            style={{ width: `${status?.progress_percentage || 0}%` }}
          ></div>
        </div>
      </div>

      {/* Current Step */}
      <div className="mb-8">
        <div className="flex items-center space-x-3 p-4 bg-primary-50 rounded-lg">
          {getStepIcon(status?.current_step)}
          <div>
            <h3 className="font-medium text-primary-900">Current Step</h3>
            <p className="text-primary-700">{status?.current_step || 'Initializing...'}</p>
          </div>
        </div>
      </div>

      {/* Timeline */}
      <div className="mb-8">
        <h3 className="font-medium text-gray-900 mb-4">Analysis Timeline</h3>
        <div className="space-y-4">
          {steps.map((step, index) => {
            const isCompleted = index <= currentStepIndex;
            const isCurrent = index === currentStepIndex;
            
            return (
              <div key={step.key} className="flex items-center space-x-4">
                <div className={`flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center ${
                  isCompleted 
                    ? 'bg-success-500 text-white' 
                    : 'bg-gray-200 text-gray-500'
                }`}>
                  {isCompleted ? (
                    <CheckCircle className="h-5 w-5" />
                  ) : (
                    <span className="text-sm font-medium">{index + 1}</span>
                  )}
                </div>
                <div className={`flex-1 ${
                  isCurrent ? 'text-primary-600 font-medium' : 
                  isCompleted ? 'text-gray-900' : 'text-gray-500'
                }`}>
                  {step.name}
                </div>
                {isCurrent && (
                  <div className="animate-pulse">
                    <div className="w-2 h-2 bg-primary-600 rounded-full"></div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>

      {/* Timing Information */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
        <div className="text-center p-3 bg-gray-50 rounded-lg">
          <div className="font-medium text-gray-900">Started</div>
          <div className="text-gray-600">
            {status?.started_at 
              ? new Date(status.started_at).toLocaleTimeString()
              : 'Pending'
            }
          </div>
        </div>
        <div className="text-center p-3 bg-gray-50 rounded-lg">
          <div className="font-medium text-gray-900">Duration</div>
          <div className="text-gray-600">
            {status?.started_at && status?.completed_at
              ? `${Math.round((new Date(status.completed_at) - new Date(status.started_at)) / 1000)}s`
              : status?.started_at
              ? `${Math.round((new Date() - new Date(status.started_at)) / 1000)}s`
              : '0s'
            }
          </div>
        </div>
        <div className="text-center p-3 bg-gray-50 rounded-lg">
          <div className="font-medium text-gray-900">Estimated Time</div>
          <div className="text-gray-600">~5 minutes</div>
        </div>
      </div>

      {/* Error Message */}
      {status?.error_message && (
        <div className="mt-6 p-4 bg-error-50 border border-error-200 rounded-lg">
          <div className="flex items-center space-x-2 mb-2">
            <XCircle className="h-5 w-5 text-error-500" />
            <h4 className="font-medium text-error-900">Error</h4>
          </div>
          <p className="text-error-700 text-sm">{status.error_message}</p>
        </div>
      )}
    </div>
  );
};

export default ProgressTracker;
