import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useQuery } from 'react-query';
import { toast } from 'react-hot-toast';
import { 
  Download, 
  BarChart3, 
  FileText, 
  Code, 
  CheckCircle,
  XCircle,
  ArrowLeft,
  ExternalLink
} from 'lucide-react';
import { analysisAPI } from '../services/api';

const ResultsPage = () => {
  const { taskId } = useParams();
  const navigate = useNavigate();
  const [isDownloading, setIsDownloading] = useState(false);

  const { data: results, error, isLoading } = useQuery(
    ['analysisResults', taskId],
    () => analysisAPI.getAnalysisResults(taskId),
    {
      retry: false,
    }
  );

  const handleDownload = async (type) => {
    setIsDownloading(true);
    try {
      let data;
      let filename;
      
      if (type === 'tests') {
        data = await analysisAPI.downloadTestFiles(taskId);
        filename = `test_files_${taskId}.zip`;
      } else {
        data = await analysisAPI.downloadCoverageReport(taskId);
        filename = `coverage_report_${taskId}.zip`;
      }
      
      // Create download link
      const url = window.URL.createObjectURL(new Blob([data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', filename);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
      
      toast.success(`${type === 'tests' ? 'Test files' : 'Coverage report'} downloaded successfully!`);
    } catch (error) {
      console.error('Download error:', error);
      toast.error('Failed to download files');
    } finally {
      setIsDownloading(false);
    }
  };

  if (isLoading) {
    return (
      <div className="card max-w-4xl mx-auto">
        <div className="text-center py-8">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading analysis results...</p>
        </div>
      </div>
    );
  }

  if (error) {
    let errorMessage = 'An error occurred while loading the results';
    
    if (error.response?.data) {
      if (typeof error.response.data === 'string') {
        errorMessage = error.response.data;
      } else if (error.response.data.detail) {
        errorMessage = error.response.data.detail;
      } else if (error.response.data.message) {
        errorMessage = error.response.data.message;
      }
    } else if (error.message) {
      errorMessage = error.message;
    }
    
    return (
      <div className="card max-w-4xl mx-auto">
        <div className="text-center py-8">
          <XCircle className="h-12 w-12 text-error-500 mx-auto mb-4" />
          <h3 className="text-lg font-semibold text-gray-900 mb-2">Error Loading Results</h3>
          <p className="text-gray-600 mb-4">{errorMessage}</p>
          <button
            onClick={() => navigate('/')}
            className="btn-primary"
          >
            Back to Home
          </button>
        </div>
      </div>
    );
  }

  if (!results) {
    return (
      <div className="card max-w-4xl mx-auto">
        <div className="text-center py-8">
          <p className="text-gray-600">No results found</p>
          <button
            onClick={() => navigate('/')}
            className="btn-primary mt-4"
          >
            Back to Home
          </button>
        </div>
      </div>
    );
  }

  const getCoverageColor = (coverage) => {
    if (coverage >= 80) return 'text-success-600';
    if (coverage >= 60) return 'text-warning-600';
    return 'text-error-600';
  };

  const getCoverageBadgeColor = (coverage) => {
    if (coverage >= 80) return 'bg-success-100 text-success-800';
    if (coverage >= 60) return 'bg-warning-100 text-warning-800';
    return 'bg-error-100 text-error-800';
  };

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="card">
        <div className="flex items-center justify-between mb-6">
          <div>
            <button
              onClick={() => navigate('/')}
              className="flex items-center text-gray-600 hover:text-gray-900 transition-colors duration-200"
            >
              <ArrowLeft className="h-4 w-4 mr-2" />
              Back to Home
            </button>
          </div>
          <div className="flex items-center space-x-2">
            <CheckCircle className="h-6 w-6 text-success-500" />
            <span className="text-lg font-semibold text-gray-900">Analysis Completed</span>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
          <div className="text-center p-4 bg-gray-50 rounded-lg">
            <div className="text-2xl font-bold text-gray-900">{results.total_files}</div>
            <div className="text-sm text-gray-600">Total Files</div>
          </div>
          <div className="text-center p-4 bg-gray-50 rounded-lg">
            <div className="text-2xl font-bold text-gray-900">{results.generated_tests}</div>
            <div className="text-sm text-gray-600">Tests Generated</div>
          </div>
          <div className="text-center p-4 bg-gray-50 rounded-lg">
            <div className="text-2xl font-bold text-gray-900">{results.detected_languages.length}</div>
            <div className="text-sm text-gray-600">Languages</div>
          </div>
          <div className="text-center p-4 bg-gray-50 rounded-lg">
            <div className={`text-2xl font-bold ${getCoverageColor(results.coverage_percentage)}`}>
              {results.coverage_percentage.toFixed(1)}%
            </div>
            <div className="text-sm text-gray-600">Coverage</div>
          </div>
        </div>

        {/* Repository Info */}
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-2">Repository</h3>
          <div className="flex items-center space-x-2">
            <Code className="h-4 w-4 text-gray-500" />
            <a
              href={results.repository_url}
              target="_blank"
              rel="noopener noreferrer"
              className="text-primary-600 hover:text-primary-700 underline flex items-center"
            >
              {results.repository_url}
              <ExternalLink className="h-3 w-3 ml-1" />
            </a>
          </div>
        </div>

        {/* Download Buttons */}
        <div className="flex flex-col sm:flex-row gap-4">
          <button
            onClick={() => handleDownload('tests')}
            disabled={isDownloading}
            className="btn-primary flex items-center justify-center space-x-2 disabled:opacity-50"
          >
            <Download className="h-4 w-4" />
            <span>{isDownloading ? 'Downloading...' : 'Download Test Files'}</span>
          </button>
          <button
            onClick={() => handleDownload('coverage')}
            disabled={isDownloading}
            className="btn-secondary flex items-center justify-center space-x-2 disabled:opacity-50"
          >
            <BarChart3 className="h-4 w-4" />
            <span>{isDownloading ? 'Downloading...' : 'Download Coverage Report'}</span>
          </button>
        </div>
      </div>

      {/* Coverage Summary */}
      <div className="card">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Coverage Summary</h3>
        
        <div className="mb-6">
          <div className="flex items-center justify-between mb-2">
            <span className="text-sm font-medium text-gray-700">Overall Coverage</span>
            <span className={`text-sm font-medium ${getCoverageColor(results.coverage_percentage)}`}>
              {results.coverage_percentage.toFixed(1)}%
            </span>
          </div>
          <div className="w-full bg-gray-200 rounded-full h-2">
            <div
              className={`h-2 rounded-full ${
                results.coverage_percentage >= 80 ? 'bg-success-500' :
                results.coverage_percentage >= 60 ? 'bg-warning-500' : 'bg-error-500'
              }`}
              style={{ width: `${Math.min(results.coverage_percentage, 100)}%` }}
            ></div>
          </div>
        </div>

        {results.coverage_report && Object.keys(results.coverage_report).length > 0 && (
          <div className="space-y-4">
            <h4 className="font-medium text-gray-900">Language Breakdown</h4>
            {Object.entries(results.coverage_report).map(([language, data]) => (
              <div key={language} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                <div className="flex items-center space-x-3">
                  <div className="w-8 h-8 bg-primary-100 rounded-lg flex items-center justify-center">
                    <Code className="h-4 w-4 text-primary-600" />
                  </div>
                  <div>
                    <div className="font-medium text-gray-900 capitalize">{language}</div>
                    <div className="text-sm text-gray-600">
                      {data.tests_passed || 0} passed, {data.tests_failed || 0} failed
                    </div>
                  </div>
                </div>
                <div className={`px-3 py-1 rounded-full text-sm font-medium ${getCoverageBadgeColor(data.coverage || 0)}`}>
                  {(data.coverage || 0).toFixed(1)}%
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Test Files Summary */}
      {results.test_files && Object.keys(results.test_files).length > 0 && (
        <div className="card">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Generated Test Files</h3>
          
          {Object.entries(results.test_files).map(([language, data]) => (
            <div key={language} className="mb-6 last:mb-0">
              <div className="flex items-center space-x-2 mb-3">
                <Code className="h-4 w-4 text-primary-600" />
                <h4 className="font-medium text-gray-900 capitalize">{language}</h4>
                <span className="text-sm text-gray-600">({data.framework})</span>
              </div>
              
              {data.files && data.files.length > 0 ? (
                <div className="space-y-2">
                  {data.files.map((file, index) => (
                    <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <div className="flex items-center space-x-3">
                        <FileText className="h-4 w-4 text-gray-500" />
                        <div>
                          <div className="font-medium text-gray-900">{file.test_file}</div>
                          <div className="text-sm text-gray-600">
                            Tests: {file.test_count || 0}
                          </div>
                        </div>
                      </div>
                      <div className="text-sm text-gray-600">
                        {file.coverage_estimate || 0}% coverage
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-gray-600 text-sm">No test files generated</div>
              )}
            </div>
          ))}
        </div>
      )}

      {/* Analysis Summary */}
      {results.analysis_summary && (
        <div className="card">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Analysis Summary</h3>
          <div className="bg-gray-50 p-4 rounded-lg">
            <pre className="text-sm text-gray-700 whitespace-pre-wrap">{results.analysis_summary}</pre>
          </div>
        </div>
      )}

      {/* Timing Information */}
      <div className="card">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Timing Information</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="text-center p-3 bg-gray-50 rounded-lg">
            <div className="font-medium text-gray-900">Started</div>
            <div className="text-gray-600">
              {results.started_at 
                ? new Date(results.started_at).toLocaleString()
                : 'N/A'
              }
            </div>
          </div>
          <div className="text-center p-3 bg-gray-50 rounded-lg">
            <div className="font-medium text-gray-900">Completed</div>
            <div className="text-gray-600">
              {results.completed_at 
                ? new Date(results.completed_at).toLocaleString()
                : 'N/A'
              }
            </div>
          </div>
          <div className="text-center p-3 bg-gray-50 rounded-lg">
            <div className="font-medium text-gray-900">Duration</div>
            <div className="text-gray-600">
              {results.started_at && results.completed_at
                ? `${Math.round((new Date(results.completed_at) - new Date(results.started_at)) / 1000)}s`
                : 'N/A'
              }
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResultsPage;
